<script>

	$('.showAllPage').css({'opacity':'0.3'});
	$('.showAllPage').hover(function(){$('.showAllPage').css({'opacity':'1'});},function(){$('.showAllPage').css({'opacity':'0.3'});});
	
	$('.curPage').css({'opacity':'0.3'});
	$('.curPage').hover(function(){$('.curPage').css({'opacity':'1'});},function(){$('.curPage').css({'opacity':'0.3'});});
	
	$('.previous_page').css({'opacity':'0.3'});
	$('.previous_page').hover(function(){$('.previous_page').css({'opacity':'1'});},function(){$('.previous_page').css({'opacity':'0.3'});});
	
	$('.next_page').css({'opacity':'0.3'});
	$('.next_page').hover(function(){$('.next_page').css({'opacity':'1'});},function(){$('.next_page').css({'opacity':'0.3'});});
	
	function bangla_num(str)
	{
		str = str.replace(/0/g,"০");
		str = str.replace(/1/g,"১");
		str = str.replace(/2/g,"২");
		str = str.replace(/3/g,"৩");
		str = str.replace(/4/g,"৪");
		str = str.replace(/5/g,"৫");
		str = str.replace(/6/g,"৬");
		str = str.replace(/7/g,"৭");
		str = str.replace(/8/g,"৮");
		str = str.replace(/9/g,"৯");
		
		return str;
	}
	
	$('.showAllPage').click(function(){$('#allPages').fadeIn(1000)});
	
	function next_page(page_num)
	{
		var total_page = ($("#totalpage").val());
		var page_num = $("#pageno").val();
		page_num = parseInt(page_num) + 1;
		
		$('.pageS'+(page_num-1)).css({'color':'#3D3D3D','background':'#E1E1E1','border':'1px solid #828282'});
		$('.pageS'+page_num).css({'color':'#FFF','background':'#FFA600','border':'1px solid #FB4704'});
		
		var str = String(page_num)
		
		$(".cur_pageno").val(bangla_num(str));
		
		$("#pageno").val(page_num);
		
		if (page_num == 1)
			$(".previous_page").fadeOut(500);
		else
			$(".previous_page").fadeIn(500);

		if (page_num == total_page)
			$(".next_page").fadeOut(500);
		else
			$(".next_page").fadeIn(500);
		
		if (page_num < 10)
			page_num = '0' + page_num;
		
		$('#allPages').hide();
		
		var pageUrl = "epaper9bd4.html?page_num="+page_num+"&date=2018_09_21";
		$.ajax({
			//url:pageUrl+'?CLICK=yes',
			url:pageUrl,
			beforeSend: function(){
				$('#epaperLoader').css('display','inline-block');
				$('#epaper').css('opacity','0.6');
			},
			success: function(data){
				$('#epaperLoader').fadeOut();
				$('#epaper').html(data);
				$('#epaper').animate({'opacity': '+1.0'});
			}
		});
		//$("#epaper").load("epaper.php?page_num="+page_num+"&date=");
	}
	
	function previous_page(page_num)
	{
		var total_page = ($("#totalpage").val());
		var page_num = $("#pageno").val();
		page_num = parseInt(page_num) - 1;
		
		$('.pageS'+(page_num+1)).css({'color':'#3D3D3D','background':'#E1E1E1','border':'1px solid #828282'});
		$('.pageS'+page_num).css({'color':'#FFF','background':'#FFA600','border':'1px solid #FB4704'});
		
		var str = String(page_num);
		
		$(".cur_pageno").val(bangla_num(str));
		
		$("#pageno").val(page_num);
		
		if (page_num == 1)
			$(".previous_page").fadeOut(500);
		else
			$(".previous_page").fadeIn(500);

		if (page_num == total_page)
			$(".next_page").fadeOut(500);
		else
			$(".next_page").fadeIn(500);
		
		if (page_num < 10)
			page_num = '0' + page_num;
			
		$('#allPages').hide();
		
		var pageUrl = "epaper9bd4.html?page_num="+page_num+"&date=2018_09_21";
		$.ajax({
			//url:pageUrl+'?CLICK=yes',
			url:pageUrl,
			beforeSend: function(){
				$('#epaperLoader').css('display','inline-block');
				$('#epaper').css('opacity','0.6');
			},
			success: function(data){
				$('#epaperLoader').fadeOut();
				$('#epaper').html(data);
				$('#epaper').animate({'opacity': '+1.0'});
			}
		});
		//$("#epaper").load("epaper.php?page_num="+page_num+"&date=");
	}

	$(".previous_page").hide();
	$(".cur_pageno").val(bangla_num("1"));
