<script>
	var img_new = 0;
    var img_old = -1;
	var totalTabloid = 2;
	
	window.setTimeout("Tick()", 100);

	for(i=0; i <2;i++)
		$("#magazineImg"+i).hide();
	$("#magazineImg0").fadeIn(700);
	
    function Tick() {
        window.setTimeout("Tick()", 5000);
        img_slide(img_new,img_old);
    }

    function img_slide(i,j)
    {
        $("#magazineImg"+i).fadeIn(700);
		$("#magazineImg"+j).fadeOut(100);
		
		img_new = i + 1;
		img_old = i;
		
		if (img_new == totalTabloid)
		    img_new = 0;
		else if(img_old == totalTabloid)
		    img_old = -1;
    }
</script>

