<script>
	function goto_Page(pageNum)
	{
		var total_page = ($("#totalpage").val());
		var page_num = $("#pageno").val();
		
		$('.pageS'+parseInt(page_num)).css({'color':'#3D3D3D','background':'#E1E1E1','border':'1px solid #828282'});
		
		page_num = parseInt(pageNum);
		
		$('.pageS'+page_num).css({'color':'#FFF','background':'#FFA600','border':'1px solid #FB4704'});
		
		var str = String(page_num)
		$(".cur_pageno").val(bangla_num(str));
		$("#pageno").val(page_num);
		
		if (page_num == 1)
			$(".previous_page").fadeOut(500);
		else
			$(".previous_page").fadeIn(500);

		if (page_num == total_page)
			$(".next_page").fadeOut(500);
		else
			$(".next_page").fadeIn(500);
		
		if (page_num < 10)
			page_num = '0' + page_num;
		
		$("#epaper").load("epaper9bd4.html?page_num="+page_num+"&date=2018_09_21");
		
		$('#allPages').fadeOut(1000);
		//alert(pageNum);
	}
